(41, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-11-22 06:23:29', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Chọn giao diện?', 'Kiểm tra các giao diện tương thích với WooCommerce và chọn một giao diện phù hợp với thương hiệu và nhu cầu kinh doanh của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-24 01:15:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cách tùy chỉnh danh mục sản phẩm của bạn', 'Bạn muốn danh mục sản phẩm và hình ảnh của mình trông đẹp mắt và phù hợp với thương hiệu của bạn. Hướng dẫn này sẽ cung cấp cho bạn tất cả các mẹo bạn cần để sản phẩm của bạn trông đẹp mắt trong cửa hàng của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-24 01:15:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bạn đã sẵn sàng để ra mắt cửa hàng của mình?', 'Để đảm bảo bạn không bao giờ có cảm giác chìm đắm \"mình đã quên điều gì\", chúng tôi đã tập hợp danh sách kiểm tra trước khi ra mắt cần thiết.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-24 01:15:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-mobile-app', 'info', 'en_US', 'Cài đặt ứng dụng di động Woo', 'Cài đặt ứng dụng di động WooCommerce để quản lý đơn đặt hàng, nhận thông báo bán hàng và xem các số liệu chính - mọi lúc mọi nơi.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-24 07:59:09', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-orders-milestone', 'info', 'en_US', 'Chúc mừng bạn đã xử lý 10 đơn đặt hàng!', 'Bạn đã đạt đến cột mốc 10 đơn đặt hàng! Nhìn bạn đi. Duyệt qua một số câu chuyện thành công của WooCommerce để lấy cảm hứng.', '{\"current_milestone\":10}', 'unactioned', 'woocommerce-admin', '2024-11-26 23:23:34', NULL, 0, 'plain', '', 0, 0, 'info');
